"); if (targetId > -1) { var targetComps = GameplayManager.Instance.GetUnitComponentsServerAndClient(targetId); if (targetComps == null) { str.Append($"Target: null
"); return str.ToString(); } Transform targetTr = targetComps.HealthController.transform; str.Append($"Target: {targetTr.name}
"); str.Append($"Distance: {Mathf.Sqrt(Vector3.SqrMagnitude(_aiUnit.transform.position - targetTr.position))} ({_aiUnit.IsTargetWithinAttackDistance()})
"); } else { str.Append($"Target: -
